import Header from "../components/Header";
import Footer from "../components/Footer";
import BestProductsList from "../components/Best_Products/BestProductsList";
import ProductList from "../components/Product_List/ProductList";

const HomePage = () => 
{
    const {setProducts} = useContext(ResortContext);
     
useEffect(()=>{


  //GET, POST,PUT, DELETE from our Front-End TO our Back

  fetch(`${process.env.REACT_APP_BACK_END_API_DOMAIN}/products`)
  .then(response=>response.json())
  .then(json=>{

        //We updated the data returned from the Backed with the resort state
        setProducts(json.data)

  })
  .catch(err=>{
      console.log(`Error ${err}`)
  })


},[])
    return (
    <div className="grid grid-row-3" id="main-container">  
            <Header/>
            <main>
                <BestProductsList/>
                <ProductList/>
            </main>
            <Footer/>
        </div>
    )
}
export default HomePage
