import React,{useContext} from 'react';
import '@splidejs/splide/dist/css/splide.min.css';
import HeroItemCard from "./HeroItemCard";
import ProductContext from '../context/ProductContext';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import Space from '../assets/img/space.jpeg';
import { Carousel } from 'rsuite';

import {Link} from "react-router-dom"
<link rel="stylesheet" href="path-to-the-file/splide.min.css"></link>
new Splide( '.splide', {
  fixedWidth : '12rem',
  fixedHeight: '6rem',
  gap        : '1rem',
} );
const Hero = () => {

    const {products} = useContext(ProductContext);
    
    return (
        <div className= "container">
          <h1> Splide Section </h1>
        <Splide>
        {products.map((product,index)=>( <SplideSlide> 
          <img src={product.product_photo} alt={product.title}/></SplideSlide>)
          )}
        </Splide>
      </div>
    )
}

export default Hero
