import {useEffect,useContext} from "react";
import BestProductItemCard from './BestProductItemCard';
import ProductContext from '../context/ProductContext';

const BestProductList = () => {
        const {setProducts} = useContext(ProductContext);
        const {BestProducts} = useContext(ProductContext);
        
        useEffect(()=>{
      
      
          //GET, POST,PUT, DELETE from our Front-End TO our Back
      
          fetch(`${process.env.REACT_APP_BACK_END_API_DOMAIN}/products?best_seller=yes`)
          .then(response=>response.json())
          .then(json=>{
      
                //We updated the data returned from the Backed with the resort state
                  setProducts(json.data)
      
          })
          .catch(err=>{
              console.log(`Error ${err}`)
          })
          
},[])

    return (
        
        <section id="hero-section">

            <div className= "container">

                <h1>Best Products</h1>
            
                <div className="grid grid-gap-1 grid-row-gap-2 grid-col-4">
                
                    {BestProducts.map((BestProducts,index)=>( <BestProductItemCard  key={index} id={BestProducts._id} title={BestProducts.product_name} image={BestProducts.product_photo} price={BestProducts.product_price} best={BestProducts.best_seller ? true : false} desc={BestProducts.description} category={BestProducts.category}/>))}
                    
                </div>
              
            </div>
            
        </section>
        
    )
}

export default BestProductList
