import '@splidejs/splide/dist/css/splide.min.css';
import ProductItemCard from "./ProductItemCard";
import ProductContext from '../context/ProductContext';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import Shoes from '../assets/img/ShoeCategory.png';
import Space from '../assets/img/space.jpeg';
import {Link} from "react-router-dom"
<link rel="stylesheet" href="path-to-the-file/splide.min.css"></link>
  

const ProductList = () => {

    const {products} = useContext(ProductContext);

    return (
        <div className= "container">
        <Splide>
        <SplideSlide>
        <ProductItemCard  key={index} id={product._id} title={product.product_name} image={product.product_photo} price={product.product_price} best={product.best_seller ? true : false} desc={product.description} category={product.category}/>))
        </SplideSlide>
        <SplideSlide>
          <img src={Space} alt="outdoor"/>
        </SplideSlide>
      </Splide>
      </div>
    )
}

export default ProductList
