import Header from "../components/Header";
import Footer from "../components/Footer";
import {useEffect,useState} from "react";
import {useParams} from "react-router-dom"

const ResortDescriptonPage = () => {


    const {id } = useParams();

    const [product, setProduct] = useState({
        title:"",
        imagePath:"",
        description:"",
        price :0

    })


    useEffect(()=>{

        //communicate with the backend!
        fetch(`${process.env.REACT_APP_BACK_END_API_DOMAIN}/products/${id}`)
        .then(response=>response.json())
        .then(json=>{
    
              //We updated the data returned from the Backed with the resort state
              setProduct(json.data);
    
        })
        .catch(err=>{
            console.log(`Error ${err}`)
        })
    
    
    },[])


    return (
    <div className="grid grid-row-3" id="main-container">  
        <Header/>
        <main>
            <section id="product-description-section">
                    <div className="container grid grid-col-4 grid-gap-2">
        
                        <img src={product.imagePath} alt="product Image"/>
                    
                        <div className="product-description-content-area">
                            <h2>{product.title}</h2>
                            <p>{product.price} per night</p>
                            <h3>Details</h3>
                            <p>{product.description}</p>
                        </div>
                    </div>
        
                </section>
        </main>
        <Footer/>
    </div>
    )
}

export default ResortDescriptonPage
