import React from 'react'
import {Link} from "react-router-dom"

const BestProductItemCard = (props) => {
    
return (

    <div class="item">
      <a href="#0" aria-labelledby="person1"></a>
      {props.best===true && 
            <Link to = {`/products/details/${props.id}`}> <img src={props.image} alt={props.title}/></Link> }
           <div class="item__overlay">
           <h3 class="card__title">{props.best===true &&
                     <h3>{props.title}</h3> 
                    }</h3>  
        <div class="item__body">
        <span class="card__status">{props.best===true &&
                     <h3>${props.price}</h3> 
                    }</span>
        </div>
      </div>
    </div>   
    )
}

export default BestProductItemCard
