import React,{useContext} from 'react';
import ProductItemCard from "./ProductItemCard";
import ProductContext from '../context/ProductContext';
import Splide from '@splidejs/splide';
<link rel="stylesheet" href="path-to-the-file/splide.min.css"></link>



const ProductList = () => {

    const {products} = useContext(ProductContext);

    return (
        <div class="splide">
        <div class="splide__track">
              <ul class="splide__list">
            
                <h1>All Products</h1>


                    {products.map((product,index)=>( <ProductItemCard  key={index} id={product._id} title={product.product_name} image={product.product_photo} price={product.product_price} best={product.best_seller ? true : false} desc={product.description} category={product.category}/>))}
                        
                    </ul>
  </div>
</div>
    )
}

export default ProductList
