import React from 'react'
import {Link} from "react-router-dom"

const ProductItemCard = (props) => {
    
    return (  
        <div className="product-card-2">

            <Link to = {`/products/details/${props.id}`}> <img src={props.image} alt={props.title}/></Link> 

            <div className="ProductContent">
                
                    <h3>{props.title}</h3> 
                    ${props.price} 
                    <h4>{props.category}</h4>
                    {props.best===true &&
                    <h4> 
                        best seller!
                    </h4>
                    }


            </div>

    </div>
    )
}

export default ProductItemCard
