import React,{useContext} from 'react';
import HeroItemCard from "./HeroItemCard";
import ProductContext from '../context/ProductContext';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import Space from '../assets/img/space.jpeg';
<link rel="stylesheet" href="path-to-the-file/splide.min.css"></link>

const Hero = () => {

    const {products} = useContext(ProductContext);

    return (
        <div className= "container">
          <h1> Splide Section </h1>
        
        {products.map((product,index)=>( <HeroItemCard  key={index} id={product._id} title={product.product_name} image={product.product_photo} price={product.product_price} best={product.best_seller ? true : false} desc={product.description} category={product.category}/>))}
       
          <img src={Space} alt="outdoor"/>
       
      </div>
    )
}

export default Hero
