import React,{useState} from'react';
import {BrowserRouter as Router,
  Switch,
  Route
  } from "react-router-dom";

import HomePage from "../pages/HomePage";
import AboutPage from "../pages/AboutPage";
import ProductsPage from '../pages/ProductsPage';
import CategoryPage from '../pages/CategoryPage';
import ProductDescPage from '../pages/ProductDescPage';
import SpecificCategoryPage from '../pages/SpecificCategoryPage';

import ProductContext from '../context/ProductContext';

import "../assets/css/App.css";
import "../assets/css/utilities.css";


const App = () => {
  var splide = new Splide( '.splide', {
    type     : 'loop',
    height   : '10rem',
    focus    : 'center',
    autoWidth: true,
  } );
  const [products , setProducts] = useState([{}]);
;

  return (


          <Router>
            <ProductContext.Provider value ={{products,setProducts}}>
            <Switch>
                    <Route exact path="/">
                        <HomePage />

                    </Route>
                    <Route exact path="/products">
                        <ProductsPage />

                    </Route>
                    <Route exact path="/about">
                        <AboutPage />

                    </Route>
                    <Route exact path="/Category">
                        <CategoryPage />

                    </Route>
                    <Route exact path="/products/details/:id">
                        <ProductDescPage />

                    </Route>
                    <Route exact path="/products?category=category">
                        <SpecificCategoryPage />

                    </Route>
                </Switch> 


            </ProductContext.Provider>
          </Router>


  )
}

export default App
