import React,{useContext} from 'react';
import ProductItemCard from "./ProductCategoryCard";
import CategoryContext from '../context/CategoryContext';
import {useParams} from "react-router-dom"


const ProductCategoryList = () => {
    const [CategoryProducts , setCategoryProducts] = useState([{}]);
    const {category } = useParams();
        
    useEffect(()=>{
  
  
      //GET, POST,PUT, DELETE from our Front-End TO our Back
  
      fetch(`${process.env.REACT_APP_BACK_END_API_DOMAIN}/products?${category}`)
      .then(response=>response.json())
      .then(json=>{
  
            //We updated the data returned from the Backed with the resort state
            setCategoryProducts(json.data)
  
      })
      .catch(err=>{
          console.log(`Error ${err}`)
      })
      
},[])


    return (
        <section id="hero-section">

            <div className= "container">
            
                <h1>All Products</h1>

                <div className="grid grid-gap-1 grid-row-gap-2 grid-col-4">

                    {CategoryProducts.map((product,index)=>( <ProductItemCard  key={index} category={product.category}/>))}
                        
                </div>
                
            </div>
            
        </section>
    )
}

export default ProductCategoryList
