import React,{useEffect,useContext} from "react";
import ProductItemCard from "./ProductItemCard";
import ProductContext from '../context/ProductContext';
import BestProductContext from '../context/BestProductContext';


const ProductList = () => {

    const {setAllProducts} = useContext(ProductContext);
    const {AllProducts} = useContext(BestProductContext);
    const {products} = useContext(ProductContext);
    useEffect(()=>{
  
  
      //GET, POST,PUT, DELETE from our Front-End TO our Back
  
      fetch(`${process.env.REACT_APP_BACK_END_API_DOMAIN}/products`)
      .then(response=>response.json())
      .then(json=>{
  
            //We updated the data returned from the Backed with the resort state
            setAllProducts(json.data)
  
      })
      .catch(err=>{
          console.log(`Error ${err}`)
      })
    },[])

    return (
        <section id="section-product-list">

            <div className= "container">
            
                <h1>All Products</h1>

                <div className="grid grid-gap-1 grid-row-gap-2 grid-col-4">

                    {Products.map((product,index)=>( <ProductItemCard  key={index} id={AllProducts._id} title={AllProducts.product_name} image={AllProducts.product_photo} price={AllProducts.product_price} best={AllProducts.best_seller ? true : false} desc={AllProducts.description} category={AllProducts.category}/>))}
                        
                </div>
                
            </div>
            
        </section>
    )
}

export default ProductList
