import {useEffect,useContext} from "react";
import BestProductContext from '../context/BestProductContext';
import ProductContext from '../context/ProductContext';
import React from 'react'
import {Link} from "react-router-dom"

const BestProductItemCard = (props) => {
    const {setProducts} = useContext(BestProductContext);
    const {products} = useContext(ProductContext);
    
    useEffect(()=>{
  
  
      //GET, POST,PUT, DELETE from our Front-End TO our Back
  
      fetch(`${process.env.REACT_APP_BACK_END_API_DOMAIN}/products?best_seller=yes`)
      .then(response=>response.json())
      .then(json=>{
  
            //We updated the data returned from the Backed with the resort state
            setProducts(json.data)
  
      })
      .catch(err=>{
          console.log(`Error ${err}`)
      })
      
},[])
    return (
        
        <div className="product-card">
            
                <div className="ProductContents">
                    {props.best===true && <Link to = {`/products/details/${props.id}`}> <img src={props.image} alt={props.title}/></Link> }

                    {props.best===true &&
                     <h3>{props.title}</h3> 
                    }
                    
                    {props.best===true &&
                     <h3>${props.price}</h3> 
                    }
                </div>
                   
            
        
    </div>
    )
}

export default BestProductItemCard
