import React,{useContext} from 'react';
    
import { Splide, SplideSlide } from '@splidejs/react-splide';
import '@splidejs/splide/dist/css/splide.min.css';

import BestProductItemCard from './BestProductItemCard';
import ProductContext from '../context/ProductContext';

const ProductList = () => {
    new Splide( '.splide', {
        type   : 'loop',
        perPage: 3,
      } );

    const {products} = useContext(ProductContext);

    return (
        
        <section id="hero-section">

            <div className= "container">

                <h1>Popular Products</h1>
                <Splide>
                <SplideSlide>
                <div className="grid grid-gap-1 grid-row-gap-2 grid-col-4">
                
                    {products((product,index)=>( <BestProductItemCard  key={index} id={product._id} title={product.product_name} image={product.product_photo} price={product.product_price} best={product.best_seller ? true : false} desc={product.description} category={product.category}/>))}
                    
                </div>
                </SplideSlide>
                </Splide> 
            </div>
            
        </section>
        
    )
}

export default ProductList
