import React,{useEffect,useContext} from "react";
import ProductItemCard from "./ProductItemCard";
import ProductContext from '../context/ProductContext';



const ProductList = () => {

    const {setAllProducts} = useContext(ProductContext);
    const {products} = useContext(ProductContext);
    
    useEffect(()=>{
  
        const {category } = useParams();
      //GET, POST,PUT, DELETE from our Front-End TO our Back
  
      fetch(`${process.env.REACT_APP_BACK_END_API_DOMAIN}/products?${category}`)
      .then(response=>response.json())
      .then(json=>{
  
            //We updated the data returned from the Backed with the resort state
            setAllProducts(json.data)
  
      })
      .catch(err=>{
          console.log(`Error ${err}`)
      })
    },[])

    return (
        <section id="section-product-list">

            <div className= "container">
            
                <h1>All Products</h1>

                <div className="grid grid-gap-1 grid-row-gap-2 grid-col-4">

                    {products.map((product,index)=>( <ProductItemCard  key={index} id={product._id} title={product.product_name} image={product.product_photo} price={product.product_price} best={product.best_seller ? true : false} desc={product.description} category={product.category}/>))}
                        
                </div>
                
            </div>
            
        </section>
    )
}

export default ProductList
