import {useEffect,useContext} from "react";
import { Splide, SplideSlide } from '@splidejs/react-splide';
import '@splidejs/splide/dist/css/splide.min.css';

import BestProductItemCard from './BestProductItemCard';
import BestProductContext from '../context/BestProductContext';

const BestProducts = () => {
   

    const {setProducts} = useContext(BestProductContext);
    const {products} = useContext(BestProductContext);
    useEffect(()=>{
  
  
      //GET, POST,PUT, DELETE from our Front-End TO our Back
  
      fetch(`${process.env.REACT_APP_BACK_END_API_DOMAIN}/products`)
      .then(response=>response.json())
      .then(json=>{
  
            //We updated the data returned from the Backed with the resort state
              setProducts(json.data)
  
      })
      .catch(err=>{
          console.log(`Error ${err}`)
      })
      
},[])

    return (
        
        <section id="hero-section">

            <div className= "container">

                <h1>Best Products</h1>
                <Splide>
                <SplideSlide>
                <div className="grid grid-gap-1 grid-row-gap-2 grid-col-4">
                
                    {products.map((product,index)=>( <BestProductItemCard  key={index} id={product._id} title={product.product_name} image={product.product_photo} price={product.product_price} best={product.best_seller ? true : false} desc={product.description} category={product.category}/>))}
                    
                </div>
                </SplideSlide>
                </Splide> 
            </div>
            
        </section>
        
    )
}

export default BestProducts
